<?phprequire_once '../config.php';try{	//checkLogged	checkLogged();	//connect db	connectDatabase();	$category	= escapseString($_POST['category']);	$name		= escapseString($_POST['name']);	$title		= escapseString($_POST['title']);	$status		= escapseString($_POST['status']);	if(($file_name = FileHelper::checkFile($_FILES['image'], IMAGE_PATH, array(), $error)) == false)	{		//close		closeConnection();		echo jsonEncode('Cannot upload image. Error : '.$error, false);		die;	}	$query			= " INSERT INTO cars SET							`id_category` = '$category',							`name` = '$name',							`title` = '$title',							`image`	 = '$file_name',							`status` = '$status',							`date_created` = now()						";	$result = mysql_query($query);	//close	closeConnection();	if($result == false)	{		//remove image if failed		@unlink(IMAGE_PATH.$file_name);		echo jsonEncode('Cannot insert to cars. Query: '.$query, false);		die;	}	echo jsonEncode('Add success');}catch (Exception $e){	echo jsonEncode('Exception '.$e, false);}