<?phprequire_once '../config.php';try{	//checkLogged	checkLogged();	//connect db	connectDatabase();	$id		= escapseString($_POST['id']);	if(($file_name = FileHelper::checkFileGallery($_FILES['image'], GALLERY_IMAGE_PATH, array(), $error)) == false)	{		//close		closeConnection();		echo jsonEncode('Cannot upload image. Error : '.$error, false);		die;	}	$query			= " INSERT INTO car_gallery SET							`id_car` = '$id',							`image`	 = '$file_name',							`date_created` = now()						";	$result = mysql_query($query);	//close	closeConnection();	if($result == false)	{		//remove image if failed		@unlink(GALLERY_IMAGE_PATH.'thumb_'.$file_name);		@unlink(GALLERY_IMAGE_PATH.$file_name);		echo jsonEncode('Cannot insert to car gallery. Query: '.$query, false);		die;	}	echo jsonEncode('Add success');}catch (Exception $e){	echo jsonEncode('Exception '.$e, false);}