<?phprequire_once '../config.php';try{	//checkLogged	checkLogged();	//connect db	connectDatabase();	$id			= escapseString($_POST['id']);	$category	= escapseString($_POST['category']);	$name		= escapseString($_POST['name']);	$title		= escapseString($_POST['title']);	$details	= escapseString($_POST['details']);	$status		= escapseString($_POST['status']);	$file_name	= $oldImage = $_POST['oldImage'];	$replaceImage = false;	if($_FILES['image']['name'] != '')	{		if(($file_name = FileHelper::checkFile($_FILES['image'], IMAGE_PATH, array(), $error)) == false)		{			//close			closeConnection();			echo jsonEncode('Cannot upload image. Error : '.$error, false);			die;		}		$replaceImage = true;	}	$query			= " UPDATE cars SET							`id_category` = '$category',							`name` = '$name',							`title` = '$title',							`details` = '$details',							`image`	 = '$file_name',							`status` = '$status',							`date_modified` = now()						WHERE id = '$id'						";	$result = mysql_query($query);	//close	closeConnection();	if($result == false)	{		//remove uploaded image		if($replaceImage && $file_name != '') @unlink(IMAGE_PATH.$file_name);		echo jsonEncode('Cannot update record. Query: '.$query, false);		die;	}	//remove old image	if($replaceImage && $oldImage != '') @unlink(IMAGE_PATH.$oldImage);	echo jsonEncode('Record was updated success');}catch (Exception $e){	echo jsonEncode('Exception '.$e, false);}