<?phprequire_once '../config.php';try{	//checkLogged	checkLogged();	//connect db	connectDatabase();	$id			= escapseString($_POST['id']);	$oper		= $_POST['oper'];	if($oper == 'edit')	{		$text		= escapseString($_POST['text']);		$query			= " UPDATE car_properties SET								`text` = '$text'							WHERE id = '$id'							";		$result = mysql_query($query);		//close		closeConnection();		if($result == false)		{			echo jsonEncode('Cannot update record. Query is '. $query, false);			die;		}		echo jsonEncode('Record was updated success');	}	elseif($oper == 'del')	{		$query			= " DELETE FROM car_properties 	WHERE id = '$id'							";		$result = mysql_query($query);		//close		closeConnection();		if($result == false)		{			echo jsonEncode('Cannot delete record. Query is '. $query, false);			die;		}		echo jsonEncode('Record was deleted success');	}	elseif($oper == 'add')	{		$text		= escapseString($_POST['text']);		$id_car		= escapseString($_GET['id_car']);		$query			= " INSERT INTO	car_properties								SET text = '$text',									id_car = '$id_car'							";		$result = mysql_query($query);		//close		closeConnection();		if($result == false)		{			echo jsonEncode('Cannot add record. Query is '. $query, false);			die;		}		echo jsonEncode('Record was addeded success');	}}catch (Exception $e){	echo jsonEncode('Exception '.$e, false);}