<?phprequire_once '../config.php';try{	//checkLogged	checkLogged();	//connect db	connectDatabase();	$type		= escapseString($_POST['type']);	$username	= escapseString($_POST['username']);	$password	= md5($_POST['password']);	$status		= escapseString($_POST['status']);	if(isDuplicateUser($username))	{		//close		closeConnection();		echo jsonEncode($username . ' is used. Please choose another username', false);		die;	}	$query			= " INSERT INTO users SET							`type` = '$type',							`username` = '$username',							`password` = '$password',							`status` = '$status',							`date_created` = now()						";	$result = mysql_query($query);	//close	closeConnection();	if($result == false)	{		echo jsonEncode('Cannot insert to users. Query: '.$query, false);		die;	}	echo jsonEncode('Add success');}catch (Exception $e){	echo jsonEncode('Exception '.$e, false);}